import { render } from '@testing-library/react';
import { Component } from 'react';
import { MovieList } from './components/movieList/MovieList.component';
import { SearchBox } from './components/search/search.component';
import { Header } from './components/header/header.component';
import image from './assets/logo.png';
import { Card } from './components/card/card.component';
import SimpleSlider from './components/slider/slider.component';
import './App.css';
import Slider from "react-slick";

class App extends Component{
  constructor(){
    super();
    this.state = {
      movies : [],
      serachField : '',
    };
  }
  componentDidMount(){
    const url = `http://www.omdbapi.com/?s=Movies&apikey=ef773fae`;
    fetch(url)
    .then((response) => {
        return response.json();
    })
    .then((data) => {
        this.setState({
            movies : data.Search
        })

    })

}

  render(){
    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 5,
      slidesToScroll: 5
    };
    const { movies, serachField } = this.state;
    const searchFilter = movies.filter(movie => movie.Title.toLowerCase().includes(serachField.toLowerCase())
    );
    const sortedsearchFilter = searchFilter.sort((a, b) => a.Title.localeCompare(b.Title));
    const randomItem = movies[Math.floor(Math.random()*movies.length)]; 
    console.log(randomItem);
    function myFunc(){
      return randomItem;
    } 
    console.log(myFunc());



    return(
      <div className="App">

        <Header>
          <div className="logo">
            <img src={image}></img>

          </div>
          <div className="seacrh-div">
          <SearchBox
            placeholder="Search Movie" 
            handleChange={e => {
              this.setState({serachField: e.target.value});
            }} 
          />
          </div>
        </Header>
          <div>
          <h2> Single Item</h2>
          <Slider {...settings}>
            <div>
              <h3>1</h3>
            </div>
            <div>
              <h3>2</h3>
            </div>
            <div>
              <h3>3</h3>
            </div>
            <div>
              <h3>4</h3>
            </div>
            <div>
              <h3>5</h3>
            </div>
            <div>
              <h3>6</h3>
            </div>
          </Slider>
        </div>


        <MovieList movies={sortedsearchFilter} />

      </div>

    );
  }
}


export default App;
