import { render } from '@testing-library/react';
import { Component } from 'react';
import { MovieList } from './components/movieList/MovieList.component';

import './App.css';
class App extends Component{
  constructor(){
    super();
    this.state = {
      movies : [],
      serachField : '',
    };
  }
  componentDidMount(){
    const url = `http://www.omdbapi.com/?s=Movies&apikey=ef773fae`;
    fetch(url)
    .then((response) => {
        return response.json();
    })
    .then((data) => {
        this.setState({
            movies : data.Search
        })
        console.log(data);
    })
    .catch((error) => console.log(error));
}
  render(){
    const { movies, serachField } = this.state;
    const searchFilter = movies.filter(movie => movie.Title.toLowerCase().includes(serachField.toLowerCase())
    );
    return(
      <div className="App">

        <h1>Welcome to Flix!</h1>

        <MovieList movies={searchFilter} />
      </div>

    );
  }
}


export default App;
