import { render } from '@testing-library/react';
import { Component } from 'react';
import { MovieList } from './components/movieList/MovieList.component';
import { SearchBox } from './components/search/search.component';
import { Header } from './components/header/header.component';

import './App.css';
class App extends Component{
  constructor(){
    super();
    this.state = {
      movies : [],
      serachField : '',
    };
  }
  componentDidMount(){
    const url = `http://www.omdbapi.com/?s=Movies&apikey=ef773fae`;
    fetch(url)
    .then((response) => {
        return response.json();
    })
    .then((data) => {
        this.setState({
            movies : data.Search
        })

    })

}
  render(){
    const { movies, serachField } = this.state;
    const searchFilter = movies.filter(movie => movie.Title.toLowerCase().includes(serachField.toLowerCase())
    );

    const sorted = searchFilter.sort((a, b) => b - a  );
    console.log(sorted);
    return(
      <div className="App">
        <Header>
        <div className="logo">
            <h1>Flix</h1>
        </div>
        <div className="seacrh-div">
        <SearchBox
          placeholder="Search Movie" 
          handleChange={e => {
            this.setState({serachField: e.target.value});
          }} 
        />

        </div>

        </Header>



        <MovieList movies={searchFilter} />
      </div>

    );
  }
}


export default App;
