class SimpleSlider extends Component {
  constructor(){
    super();
    this.state = {
      movies : [],

    };
  }
    render() {
      const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 5
      };
      return (
        <div>
          <h2> Single Item</h2>
          <Slider {...settings}>
            {movies.map(movie => (
              <Card key={movie.imdbID} movie={movie} />
            ))}
          </Slider>
        </div>
      );
    }
  }

  export default SimpleSlider;
