import { render } from '@testing-library/react';
import { Component } from 'react';

import './App.css';
class App extends Component{
  constructor(){
    super();
    this.state = {
      movies : [],
      serachField : '',
    };
  }
  componentDidMount(){
    const url = `http://www.omdbapi.com/?s=Movies&apikey=ef773fae`;
    fetch(url)
    .then((response) => {
        return response.json();
    })
    .then((data) => {
        this.setState({
            movies : data.Search
        })
        console.log(data);
    })
    .catch((error) => console.log(error));
}
  render(){
    return(
      <div className="App">
        <h1>Hi, Jasmin!</h1>
        {
          this.state.movies.map(movie => <h1>{movie.Title}</h1> )
        }

      </div>

    )
  }
}


export default App;
