import React from 'react';
import './single-movie.styles.scss';
import CustomButton from '../custom-button/custom-button.component';
const SingleMovie = ({movies}) => {
    const oneItem = movies.find(movie => movie.Title === 'Adventures in Babysitting');
    return(
        <div className="single-movie" key={oneItem.imdbID}>
          <section className="row">
              <div className="col left">
                  <img src={oneItem.Poster} alt={oneItem.Title}></img>
                  <div className="watch-now">{oneItem.Title}</div>
              </div>
              <div className="col right">
                  <h1>{oneItem.Title}</h1>
                  <p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa.</p>
                  <CustomButton></CustomButton>
              </div>
          </section>
      </div>
    )
}

export default SingleMovie;
