import React, { Component } from "react";
import Slider from "react-slick";

class SimpleSlider extends Component {
    render() {
      const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 5
      };
      return (
        <div>
          <h2> Single Item</h2>
          <Slider {...settings}>
            <div>
             {this.children}
            </div>

          </Slider>
        </div>
      );
    }
  }

  export default SimpleSlider;
