import { render } from '@testing-library/react';
import { Component } from 'react';
import { MovieList } from './components/movieList/MovieList.component';

import './App.css';
class App extends Component{
  constructor(){
    super();
    this.state = {
      movies : [],
      serachField : '',
    };
  }
  componentDidMount(){
    const url = `http://www.omdbapi.com/?s=Movies&apikey=ef773fae`;
    fetch(url)
    .then((response) => {
        return response.json();
    })
    .then((data) => {
        this.setState({
            movies : data.Search
        })
        console.log(data);
    })
    .catch((error) => console.log(error));
}
  render(){
    const { movies, serachField } = this.state;
    const searchFilter = movies.filter(movie => movie.Title.toLowerCase().includes(serachField.toLowerCase()))
    return(
      <div className="App">

        <h1>Welcome to Flix!</h1>
        <input 
          type="search" 
          placeholder="Search Movie" 
          onChange={e => {
            this.setState({serachField: e.target.value});
          }} 

        />
        <MovieList movies={this.state.movies} />
      </div>

    );
  }
}


export default App;
